-- All weapons silenced and high concealment-- Author: DvD local suppressed = true		-- Set to true if you want every weapon to be silenced, regardless of which mods are equippedlocal concealment = true	-- Set to true if you want every mod part to have a specific concealment:local concealment_value = 75	-- If 'concealment' is true, this is the concealment every mod part will have local old_init = WeaponFactoryTweakData.initfunction WeaponFactoryTweakData:init()	old_init(self)	for part_id, part in pairs(self.parts) do				if concealment then			if not part.stats then part.stats = {} end			part.stats["concealment"] = concealment_value		end				if suppressed and part and (part.type == "barrel" or part.type == "barrel_ext") and not part.sub_type then 					part.sub_type = "silencer"			if not part.stats then part.stats = {} end			part.stats["alert_size"] = 12			part.stats["suppression"] = 12			part.perks = { "silencer" }			part.sound_switch = { suppressed = "suppressed_b" }		end	endend